import React, {useEffect, useState}from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import CharacterCardCreator from './components/CharcterCardCreator.js'

function App() {
  const [data, setData] =  useState([]);
  const [query, setQuery] = useState("character/");

  useEffect(() => {
    const fetchData = () => {
      axios
        .get("https://rickandmortyapi.com/api/" + query)
        .then(res => {
          console.log(res)
          setData(res.data.results)
          console.log(data)
        })
        .catch(error =>{
          console.log("Error: ", error);
        })
    };

    fetchData();
  }, [query]);
  if(data.length == 0){
    return <h1>Loading...</h1>
  }
  return (  
    <div className="App">
      <div className="All Characters">
      <CharacterCardCreator
      image={data[0].image}
      name={data[0].name}
      species={data[0].species}
      gender={data[0].gender}
      location={data[0].location.name}
      origin={data[0].origin.name}
      >
      </CharacterCardCreator>
      </div>
    </div>
  );
}

export default App;
