import React, {useEffect, useState}from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import CharacterCard from './components/CharcterCards.js'
function App() {
  const [data, setData] = useState({});
  const [query, setQuery] = useState("character/");

  useEffect(() => {
    const fetchData = () => {
      axios
        .get("https://rickandmortyapi.com/api/" + query)
        .then(res => {
          console.log(res)
          setData(res.data.results[0])
        });
    };

    fetchData();
  }, [query]);
  return (  
    <div className="App">
      <CharacterCard
      image={data.image}
      species={data.species}
      gender={data.gender}
      location={data.location.name}
      origin={data.origin.name}
      >
      </CharacterCard>
    </div>
  );
}

export default App;
