import React, {useEffect, useState}from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import CharacterCards from './components/CharacterCards.js'
import { Spinner } from 'reactstrap';
import showMe from './images/show-me.jpg'
function App() {
  const [data, setData] =  useState([]);
  const [query, setQuery] = useState("3");

  useEffect(() => {
    const fetchData = () => {
      axios
        .get("https://rickandmortyapi.com/api/character/?page=" + query)
        .then(res => {
          console.log(res)
          setData(res.data.results)
        })
        .catch(error =>{
          console.log("Error: ", error);
        })
    };

    fetchData();
  }, [query]);
  if(data.length == 0){
    return <Spinner style={{ width: '6rem', height: '6rem' }} />
  }
  return (  
    <div className="App">
      <div className="header">
        <img src={showMe} alt="Beg head" width="200px" height="200px"/>
        <h2>Show Me What You Got</h2>
      </div>
      <h1>Dead or Alive?</h1>
      <div className="All Characters">
        <CharacterCards data={data}>
        </CharacterCards>
      </div>
    </div>
  );
}

export default App;
